import "./Button.css";
import playIcon from '../../images/icon-play.png';
import pauseIcon from '../../images/icon-pause.png';

const Button = ({ active, text, ...btnProps }) => {

  return (
    <button
      className={active ? ".btn-active" : "btn-disabled"}
      {...btnProps}
    >

    {text === 'Start' &&
      <img src={playIcon} width="30" alt="Start" />
    }
    {text === 'Pause' &&
      <img src={pauseIcon} width="30" alt="Pause" />
    }


      {text}
    </button>
  );
};

export default Button;
