import "./Button.css";
import playIcon from "icon-play.png";

const Button = ({ active, text, img, ...btnProps }) => {

  let imgPath = '';
  if(img === 'start'){imgPath = 'icon-play.png'}
  else if(img === 'pause') {imgPath = 'icon-pause.png'}

  return (
    <button
      className={active ? ".btn-active" : "btn-disabled"}
      {...btnProps}
    >
      <img src={imgPath} />
      {text}
    </button>
  );
};

export default Button;
