import { useEffect, useState } from "react";
import Panel from "../generic/Panel.js";
import Button from "../generic/Button.js";

//Some inspiration from https://stackoverflow.com/questions/61923862/how-to-pause-a-setinterval-countdown-timer-in-react

const Countdown = () => {

    const [seconds, setSeconds] = useState(180);
    const [pause, setPause] = useState(true);

    useEffect(() => {
    const interval = setInterval(() => {
        if(!pause) { 
        if (seconds > 0) {
            setSeconds(seconds - 1);
        }
        }
    }, 1000);
    return () => clearInterval(interval);
    });

    const handlePauseToggle = () => {
        setPause(!pause);
        console.log('Is paused?',pause);
    }

    return (
        <>
        <Panel className={"output"}> Second(s): {seconds}</Panel>
        <Button className={(pause)?'btn-start':'btn-pause'} text={(pause)?'Start':'Pause'} onClick={handlePauseToggle} />
        </>        
    );
};

export default Countdown;
