import { useEffect, useState } from "react";
import Panel from "../generic/Panel.js";
import Button from "../generic/Button.js";

//Some inspiration from https://stackoverflow.com/questions/61923862/how-to-pause-a-setinterval-countdown-timer-in-react

const Countdown = () => {

    const [seconds, setSeconds] = useState(180);
    const [pause, setPause] = useState(true);
    const [intervalID, setIntervalID] = useState(0);

    // useEffect(() => {

    //     const interval = setInterval(sayHi, 1000); //1000 millisecond = 1 second
    //     // const interval = setInterval(() => {
    //     //     if(!pause) { 
    //     //         if (seconds > 0) {
    //     //             setSeconds(seconds - 1);
    //     //         }
    //     //     }
    //     // }, 1000);
    // return () => clearInterval(interval);
    // });

    const sayHi = () => {
        if(!pause){
            if(seconds > 0){
                setSeconds(seconds - 1);
            }
        }
    }

    const handlePauseToggle = () => {
        setPause(!pause);
        console.log('Is paused?',pause);
    }
    const handleEnd = () => {
        setPause(false);
        setSeconds(0);
        clearInterval(intervalID);
    }

    return (
        <>
        <Panel className={"output"}>Interval: {intervalID} Second(s): {seconds}</Panel>
        <Button className={(pause)?'btn-start':'btn-pause'} text={(pause)?'Start':'Pause'} onClick={handlePauseToggle} />
        <Button className='btn-end' text='End' onClick={handleEnd} />
        </>        
    );
};

export default Countdown;
