import { useEffect, useState } from "react";
import Panel from "../generic/Panel.js";
import Button from "../generic/Button.js";

//With some help from https://stackoverflow.com/questions/61923862/how-to-pause-a-setinterval-countdown-timer-in-react

const Countdown = () => {

    const [seconds, setSeconds] = useState(180);
    const [pause, setPause] = useState(false);

    useEffect(() => {
    const interval = setInterval(() => {
        if(!pause) { //I used '!paused' because I set pause initially to false. 
        if (seconds > 0) {
            setSeconds(seconds - 1);
        }
        }
    }, 1000);
    return () => clearInterval(interval);
    });

    const handlePauseToggle = () => {
        setPause(!pause);
    }

    return (
        <>
        <Panel className={"output"}> Second(s): {currentTime}</Panel>
        <Button className='btn-start' text="Start" onClick={handleStart} />
        <Button className='btn-pause' text="Pause" onClick={handlePause} />
        <Button className='btn-end' text='End' onClick={handleEnd}/></>
    );
};

export default Countdown;
