import { useEffect, useState } from "react";
import Panel from "../generic/Panel.js";
import Button from "../generic/Button.js";

const Countdown = () => {


    const [startTime, setStartTime] = useState(0);
    const [endTime, setEndTime] = useState(180);
    const [currentTime, setCurrentTime] = useState(0);
    const [state, setState] = useState({internalID:0, status:'idle'});


    const handleStart = () => {
        start();
        console.log('Start button hit!');
    }

    const handleEnd = () => {
        end();
        console.log('End button hit.');
    }

    const sayHi = () => {
        const date = new Date();
        let curr = date.toLocaleTimeString();
        console.log('current time', curr);
        setCurrentTime(curr);
    }

    const start = () => {
         setInterval(sayHi, 1000);
    }
    const end = () => {
        clearInterval(start);   
    }


    return (
        <>
        <Panel className="output">Current Time: {currentTime}</Panel>
        <Button className='btn-start' text='Start' onClick={handleStart} />
        <Button className='btn-end' text='End' onClick={handleEnd}/></>
    );
};

export default Countdown;
